import React, { useState } from "react";
import { AppBar, Toolbar, Typography, InputBase, Grid, Card, CardMedia, CardContent, Container, Box, IconButton, MenuItem, Select } from "@mui/material";
import { Search as SearchIcon, SportsEsports as GameIcon } from "@mui/icons-material";
import {useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const games = [
  { id: 1, name: "Counter-Strike 2", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRb31-JRa9sPGeLKTqwta6nTJSfbb6XSJGjMA&s", accounts: 5 },
  { id: 2, name: "Dota 2", image: "https://i.pcmag.com/imagery/reviews/00XEmE7YBg1AOLEzZFQxhJV-3.fit_lim.size_1200x630.v1569475078.jpg", accounts: 8 },
  { id: 3, name: "PUBG", image: "data:image/jpeg;base64,", accounts: 10 },
  { id: 4, name: "GTA V", image: "data:image/jpeg;base64,", accounts: 3 },
];

const categories = ["FPS", "MOBA", "Battle Royale", "RPG", "Adventure", "Strategy", "Simulation", "Sports", "Horror", "Indie"];

const Home = () => {
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [showSearch, setShowSearch] = useState(false);
  const filteredGames = games.filter((game) => game.name.toLowerCase().includes(search.toLowerCase()));
  const navigate = useNavigate()
  return (
    <Box sx={{ backgroundColor: "#121212", minHeight: "100vh", color: "white" }}>
      
     
 <Navbar />
      {/* Mobile Search Bar */}
      {showSearch && (
        <Box sx={{ display: { xs: "flex", sm: "none" }, justifyContent: "center", background: "#1f1f1f", padding: 2 }}>
          <InputBase
            placeholder="Search games..."
            onChange={(e) => setSearch(e.target.value)}
            sx={{ background: "#333", padding: "8px", borderRadius: 2, color: "white", width: "90%" }}
          />
        </Box>
      )}

      {/* Categories Navbar */}
      <Container sx={{ mt: 2, display: "flex", gap: 2, overflowX: "auto", pb: 2, justifyContent: "center", flexWrap: "wrap" }}>
        <Typography variant="h5" sx={{ color: "#ff9800", mr: 2,paddingTop:"10px" }}>Categories:</Typography>
        <Select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          displayEmpty
          sx={{ backgroundColor: "#ff9800", borderRadius: 2, color: "black", px: 3, width: "200px" }}
        >
          <MenuItem value="">All</MenuItem>
          <MenuItem value="New">New</MenuItem>
          <MenuItem value="Popular">Popular</MenuItem>
          {categories.map((cat) => (
            <MenuItem key={cat} value={cat}>{cat}</MenuItem>
          ))}
        </Select>
      </Container>

      {/* Game Grid */}
      <Container sx={{ mt: 3,mb:3 }}>
        <Grid container spacing={3} justifyContent="center">
          {filteredGames.map((game) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={game.id}>
              <Card onClick={()=>navigate(`/${game.id}`)} sx={{ background: "#1e1e1e", borderRadius: "10px", transition: "transform 0.3s", "&:hover": { transform: "scale(1.05)" } }}>
                <CardMedia component="img" height="180" image={game.image} alt={game.name} sx={{ borderRadius: "10px 10px 0 0" }} />
                <CardContent>
                  <Typography variant="h6" sx={{ fontWeight: "bold", textAlign: "center", color: "#ff9800" }}>
                    {game.name}
                  </Typography>
                  <Typography variant="body2" color="gray" textAlign="center">
                    {game.accounts} Accounts Available
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
      <Footer />
    </Box>
  );
};

export default Home;